                        parametros.Add("@TipoLiquidacion", r.cabecera.TipoLiquidacion);
                        parametros.Add("@opcionJubilatoria", r.cabecera.OpcionJubilatoria);
                        parametros.Add("@afjp", r.cabecera.Afjp);
                        parametros.Add("@liquidacion", idLiquidacion);

